import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { clearCurrentToken, getCurrentToken } from './api'
import '../src/style.css';


import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch,
    useHistory
} from 'react-router-dom';

import {
    Home,
    Register,
    Login,
    Routines,
    MyRoutines,
    Activities
} from './components';


const App = () => {

    const history = useHistory();
//use states
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmedPassword, setConfirmedPassword] = useState('');
    const [currentUser, setCurrentUser] = useState('');

    if(!getCurrentToken()) { 
        return <Route exact path='/register'>
        <Register username={username} setUsername={setUsername} password={password} setPassword={setPassword} 
        confirmedPassword={confirmedPassword} setConfirmedPassword={setConfirmedPassword}/>
    </Route>

    } else return ( 
        <Router>

        <div className="app">
            <header className="fixedheader">
                <h1>Fitness Tracker</h1>
                <nav>
                    <Link to='/home'>Home </Link><p>|</p>
                    <Link to='/routines'>Fitness Routines</Link><p>|</p>
                    <Link to='/myroutines'>My Routines</Link><p>|</p>
                    <Link to='/activities'>Activities</Link><p>|</p>
                    <Link to='/' onClick={() => {
                        clearCurrentToken();
                        //need to get it to push to home page after logout
                        }}>Logout</Link>
                </nav>
            </header>
            <div className="headerspacer"></div>
            <main>
            <Switch>

                <Route exact path='/home'>
                    <Home />
                </Route>


                <Route exact path='/login'>
                    <Login username={username} setUsername={setUsername} password={password} setPassword={setPassword} 
                    confirmedPassword={confirmedPassword} setConfirmedPassword={setConfirmedPassword} setCurrentUser={setCurrentUser}/>
                </Route>

                <Route exact path='/routines'>
                    <Routines />
                </Route>

                <Route exact path='/myroutines'>
                    <MyRoutines />
                </Route>

                <Route exact path='/activities'>
                    <Activities />
                </Route>


            </Switch>
            </main>
    </div>
    </Router>
    )
}


ReactDOM.render(
    <Router>
         <App />
    </Router>,
    document.getElementById('app'),
  )
