import React, { Component, useState, useEffect } from "react";
import { getCurrentToken, fetchRoutines, fetchUsername } from '../api';
import ActivityForm from './ActivityForm'

export default async function Activity (props) {
    const {
        activity
    } = props;

    const [currentUser, setCurrentUser] = useState('');
    const [open, setIsOpen] = useState(false);

    const toggleActivityForm = () => {
        if (open === false) {
        setIsOpen(true);
        } else {
        setIsOpen(false);
        }
      }

      useEffect(() => {
          fetchUsername()
          .then((data) => setCurrentUser(data))
      })


    if(getCurrentToken()) {
    return (
        <div class="activity-card">
            <h4>Name: {activity.name}</h4>
            <h4>Description: {activity.description}</h4>
            <h4>Duration: {activity.duration}</h4>
            <h4>Count: {activity.count}</h4>
            <button onClick={toggleActivityForm()}>Update Activity</button>
            <ActivityForm id='activityform' actvity={activity} open={open}/>
            <button>Delete Activity</button>
        </div>
        )




    } else return (
        <div class="activity-card">
            <h4>Name: {activity.name}</h4>
            <h4>Description: {activity.description}</h4>
            <h4>Duration: {activity.duration}</h4>
            <h4>Count: {activity.count}</h4>
        </div>
    )
}
