import React, { useState, useEffect } from 'react';
import { fetchActivites } from '../api';


const addActivity = async ({activity, count, duration}) => {
    await fetch(`http://fitnesstrac-kr.herokuapp.com/api/routines/${routine.id}/activities`, {
        method: "POST",
        body: JSON.stringify({
            activityId: activity,
            count: count, 
            duration: duration
        })
}).then(response => response.json())
  .then(result => {
    console.log(result);
  })
  .catch(console.error);
}


const AddAnActivityForm = ({open, setIsOpen, routine}) => {

    console.log(routine.id)

    const [activities, setActivities] = useState([]);
    const [activity, setActivity] = useState('');
    const [duration, setDuration] = useState('');
    const [count, setCount] = useState('');
    const [activityId, setActivityId] = useState('');


    const closeForm = () => setIsOpen(false);

    useEffect(() => {
        fetchActivites()
        .then((data) => setActivities(data))
    })


console.log(activities);



if(open === true) {
        return (
            <div>
                <label htmlFor="select-activity" onSubmit={e => {
                    e.preventDefault();
                    addActivity({activity, count, duration})
                }}>Activity</label>
                <select 
                    name="activity" 
                    id="select-activity"
                    value={activity} 
                    onChange={event => {
                        setActivity(event.target.value);
                        }}>
                    {activities.map((activity, idx) => { 
                        return <option key={idx} value={activity.id}>{activity.name}</option>
                    })}
                </select>
                <input type='text' placeholder='Count' onChange={(e) => setCount(e.target.value)}></input>
                <input type='text' placeholder='Duration' onChange={(e) => setDuration(e.target.value)}></input>
                <button onClick={closeForm}>Hide</button>
                <input type='submit'></input>
            </div>
        )
    } else return <div></div>;
}

export default AddAnActivityForm;
