import React, { Component, useState, useEffect } from "react";
import { fetchRoutines, fetchUsername, getCurrentToken } from '../api';
import ActivityForm from './ActivityForm'

export default function Activity (props) {
    const {
        activity
    } = props;

    const activityId = activity.routineActivityId;

    const [currentUser, setCurrentUser] = useState('');
    const [routines, setRoutines] = useState([]);
    const [open, setIsOpen] = useState(false);

    const openForm = () => {
        if (open === false){
             setIsOpen(true);}
        else {
            setIsOpen(false)
        }
    }

    const handleDelete = () => {
        fetch(`http://fitnesstrac-kr.herokuapp.com/api/routine_activities/${activityId}`, {
            method: "DELETE",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${getCurrentToken()}`
            }
        }).then(response => response.json())
        .then(result => {
            console.log(result);
            alert('Your activity has been deleted.')
        })
        .catch(console.error);
    }

    useEffect(() => {
        fetchUsername()
        .then((data) => setCurrentUser(data.username))
    })



    return (
        <div class="activity-card">
            <h4>Name: {activity.name}</h4>
            <h4>Description: {activity.description}</h4>
            <h4>Duration: {activity.duration}</h4>
            <h4>Count: {activity.count}</h4>
        </div>
        )
}
