import React, { Component, useState } from "react";
import { getCurrentToken, fetchRoutines } from '../api';
import ActivityForm from './ActivityForm'

export default function Activity (props) {
    const {
        activity
    } = props;

    const [currentUser, setCurrentUser] = useState('');
    const [open, setIsOpen] = useState(false);

    function toggleActivityForm() {
        const x = document.getElementById('acitivityform');
        if (x.style.display === "none") {
          x.style.display = "block";
        } else {
          x.style.display = "none";
        }
      }

    const openForm = () => setIsOpen(true);


        fetch('http://fitnesstrac-kr.herokuapp.com/api/users/me', {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${getCurrentToken()}`
        },
      }).then(response => response.json())
        .then(result => {
          setCurrentUser(result.username);
        })
        .catch(console.error);


    if(getCurrentToken()) {
    return (
        <div class="activity-card">
            <h4>Name: {activity.name}</h4>
            <h4>Description: {activity.description}</h4>
            <h4>Duration: {activity.duration}</h4>
            <h4>Count: {activity.count}</h4>
            <button onClick={toggleActivityForm()}>Update Activity</button>
            <ActivityForm id='activityform' actvity={activity} open={open}/>
            <button>Delete Activity</button>
        </div>
        )




    } else return (
        <div class="activity-card">
            <h4>Name: {activity.name}</h4>
            <h4>Description: {activity.description}</h4>
            <h4>Duration: {activity.duration}</h4>
            <h4>Count: {activity.count}</h4>
        </div>
    )
}
